#include "server.h"#include "token.h"#include "common.h"#include "proposer.h"#include "block_chain.h"#include "block_discovery.h"#include "transaction_pool.h"#include "unclaimed_transaction_pool.h"static inline voidInitializeLogging(char* arg0){    using namespace Token;    google::LogToStderr();    google::InitGoogleLogging(arg0);}//TODO:// - create global environment teardown and deconstruct routines// - validity/consistency checks on block chain data// - better merkle tree implementation// - monitoring functionality?// - initialize node info// - need tech docs for points of interest// - cleanup logs// - Snapshots: https://www.geeksforgeeks.org/chandy-lamports-global-state-recording-algorithm/intmain(int argc, char** argv){    using namespace Token;    // 1. Install Signal Handlers    SignalHandlers::Initialize();    // 2. Parse Command Line Arguments    gflags::ParseCommandLineFlags(&argc, &argv, true);    // 3. Start Logging    InitializeLogging(argv[0]);    // 4. Start the allocator    Allocator::Initialize();#ifdef TOKEN_DEBUG    // Print Debug Banner in Logs    std::string header = "Token " + GetVersion() + " Debug Mode Enabled!";    size_t total_size = 50;    size_t middle = (total_size - header.size()) / 2;    std::stringstream ss1;    for(size_t idx = 0; idx < total_size; idx++) ss1 << "#";    std::stringstream ss2;    ss2 << "#";    for(size_t idx = 0; idx < middle; idx++) ss2 << " ";    ss2 << header;    for(size_t idx = 0; idx < middle - 1; idx++) ss2 << " ";    ss2 << "#";    LOG(INFO) << ss1.str();    LOG(INFO) << ss2.str();    LOG(INFO) << ss1.str();#endif //TOKEN_DEBUG    // 5. Start the Block Chain Phase    BlockChain::Initialize();#ifdef TOKEN_DEBUG    LOG(INFO) << "Unclaimed Transaction Pool:";    UnclaimedTransactionPool::PrintUnclaimedTransactions();    LOG(INFO) << "Block Chain:";    BlockChain::PrintBlockChain();#endif//TOKEN_DEBUG    BlockDiscoveryThread::Start();    ProposerThread::Start();    // 7. Start the Block Chain Server OSThreadBase    Server::Start();    // 8. Optionally start the Health Check Server OSThreadBase#ifdef TOKEN_HEALTHCHECK_SUPPORT    BlockChainHttpServer::StartServer();#endif//TOKEN_HEALTHCHECK_SUPPORT    sleep(30);//------------------------------------------------------------------------------// Test Code    LOG(INFO) << "getting unclaimed transactions";    std::vector<uint256_t> utxos;    if(!UnclaimedTransactionPool::GetUnclaimedTransactions(utxos)){        LOG(ERROR) << "couldn't get unclaimed transactions for: TestUser";        return EXIT_FAILURE;    }    uint32_t idx = 0;    for(auto& it : utxos){        LOG(INFO) << "spending token: " << it;        UnclaimedTransaction* utxo = UnclaimedTransactionPool::GetUnclaimedTransaction(it);        Transaction::InputList inputs = {            Input(utxo->GetTransaction(), utxo->GetIndex(), utxo->GetUser()),        };        Transaction::OutputList outputs = {            Output("TestUser2", "TestToken2")        };        Transaction* tx = Transaction::NewInstance(idx++, inputs, outputs);        TransactionPool::PutTransaction(tx);        if(idx == 2) break;    }//----------------------------------------------------------------------------------    // 9. Disconnect the Block Chain Server OSThreadBase    Server::WaitForState(Server::kStopped);    // 10. Disconnect the Block Miner OSThreadBase    //TODO: shutdown block discovery thread    return EXIT_SUCCESS;}